mapper.select:
select dh.number,m.`name` MName,m.model,di.unit_price,di.oper_number,di.all_price,s.supplier SName,d.dName DName, date_format(dh.oper_time, '%Y-%m-%d') OperTime, concat(dh.sub_type,dh.type) as NewType from jsh_depot_head dh inner join jsh_depot_item di on di.header_id=dh.id and ifnull(di.delete_flag,'0') !='1' inner join jsh_material m on m.id=di.material_id and ifnull(m.delete_flag,'0') !='1' inner join jsh_supplier s on s.id=dh.organ_id and ifnull(s.delete_Flag,'0') !='1' inner join (select id,name as dName,delete_Flag from jsh_depot ) d on d.id=di.depot_id and ifnull(d.delete_Flag,'0') !='1' where dh.oper_time >='${beginTime}' and dh.oper_time <='${endTime}'
and dh.organ_id = ${oId}
and di.depot_id = ${pid}
and di.depot_id in (${dids})
and dh.type='${type}'
and ifnull(dh.delete_flag,'0') !='1' ORDER BY oper_time DESC,number desc
limit #{offset},#{rows}

select count(1) from jsh_depot_head dh inner join jsh_depot_item di on di.header_id=dh.id and ifnull(di.delete_flag,'0') !='1' inner join jsh_material m on m.id=di.material_id and ifnull(m.delete_Flag,'0') !='1' inner join jsh_supplier s on s.id=dh.organ_id and ifnull(s.delete_Flag,'0') !='1' inner join (select id,name as dName,delete_Flag from jsh_depot) d on d.id=di.depot_id and ifnull(d.delete_Flag,'0') !='1' where dh.oper_time >='${beginTime}' and dh.oper_time <='${endTime}'
and dh.organ_id = ${oId}
and di.depot_id = ${pid}
and di.depot_id in (${dids})
and dh.type='${type}'
and ifnull(dh.delete_flag,'0') !='1' ORDER BY oper_time DESC,number desc

select di.material_id, m.mName,m.Model,m.categoryName,

(select sum(jdi.basic_number) numSum from jsh_depot_head jdh
INNER JOIN jsh_depot_item jdi on jdh.id=jdi.header_id and ifnull(jdi.delete_flag,'0') !='1'
where jdi.material_id=di.material_id
and jdh.type='${type}' and jdh.oper_time >='${beginTime}' and jdh.oper_time <='${endTime}'

    and jdh.organ_id = ${oId}


    and jdi.depot_id= ${pid}


    and jdi.depot_id in (${dids})

and ifnull(jdh.delete_flag,'0') !='1'
) numSum,

(select sum(jdi.all_price) priceSum from jsh_depot_head jdh
INNER JOIN jsh_depot_item jdi on jdh.id=jdi.header_id  and ifnull(jdi.delete_flag,'0') !='1'
where jdi.material_id=di.material_id
and jdh.type='${type}' and jdh.oper_time >='${beginTime}' and jdh.oper_time <='${endTime}'

    and jdh.organ_id = ${oId}


    and jdi.depot_id= ${pid}


    and jdi.depot_id in (${dids})

and ifnull(jdh.delete_flag,'0') !='1'
) priceSum
from jsh_depot_head dh
INNER JOIN jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1'
INNER JOIN (SELECT jsh_material.id,jsh_material.name mName, Model,jsh_material_category.`Name` categoryName
from jsh_material
LEFT JOIN jsh_material_category on jsh_material.category_id=jsh_material_category.Id and ifnull(jsh_material_category.status,'0') !='2'
where ifnull(jsh_material.delete_Flag,'0') !='1'
) m
on m.Id=di.material_id where dh.type='${type}' and dh.oper_time >='${beginTime}' and dh.oper_time <='${endTime}'

    and dh.organ_id = ${oId}


    and di.depot_id= ${pid}


    and di.depot_id in (${dids})

and ifnull(dh.delete_flag,'0') !='1'
GROUP BY di.material_id,m.mName,m.Model,m.categoryName

    limit #{offset},#{rows}

select count(1) from (select di.material_id, m.mName,m.Model,m.categoryName from jsh_depot_head dh INNER JOIN jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1' INNER JOIN (SELECT jsh_material.id,jsh_material.name mName, Model,jsh_material_category.`Name` categoryName from jsh_material LEFT JOIN jsh_material_category on jsh_material.category_id=jsh_material_category.Id and ifnull(jsh_material_category.status,'0') !='2' where ifnull(jsh_material.delete_Flag,'0') !='1' ) m on m.Id=di.material_id where dh.type='${type}' and dh.oper_time >='${beginTime}' and dh.oper_time <='${endTime}'
and dh.organ_id = ${oId}
and di.depot_id= ${pid}
and di.depot_id in (${dids})
and ifnull(dh.delete_flag,'0') !='1' GROUP BY di.material_id,m.mName,m.Model,m.categoryName) a

select dh.number,concat(dh.sub_type,dh.type) as type,dh.discount_last_money,dh.other_money,dh.change_amount,s.supplier supplierName, date_format(dh.oper_time,'%Y-%m-%d %H:%i:%S') as oTime from jsh_depot_head dh inner join jsh_supplier s on s.id=dh.organ_id and ifnull(s.delete_Flag,'0') !='1' where s.type='${supType}' and (dh.sub_type!='其它' and dh.sub_type!='采购订单' and dh.sub_type!='销售订单') and dh.oper_time >='${beginTime}' and dh.oper_time <='${endTime}'
and dh.organ_id=${organId}
and ifnull(dh.delete_flag,'0') !='1' UNION ALL select ah.bill_no number,ah.type as newType,ah.total_price discount_last_money, 0 other_money,ah.change_amount,s.supplier supplierName, date_format(ah.bill_time,'%Y-%m-%d %H:%i:%S') as oTime from jsh_account_head ah inner join jsh_supplier s on s.id=ah.organ_id and ifnull(s.delete_Flag,'0') !='1' where s.type='${supType}' and ah.bill_time >='${beginTime}' and ah.bill_time <='${endTime}'
and ah.organ_id=${organId}
and ifnull(ah.delete_flag,'0') !='1' ORDER BY oTime
limit #{offset},#{rows}

select sum(a) from ( select count(1) a from jsh_depot_head dh inner join jsh_supplier s on s.id=dh.organ_id and ifnull(s.delete_Flag,'0') !='1' where s.type='${supType}' and (dh.sub_type!='其它' and dh.sub_type!='采购订单' and dh.sub_type!='销售订单') and dh.oper_time >='${beginTime}' and dh.oper_time <='${endTime}'
and dh.organ_id=${organId}
and ifnull(dh.delete_flag,'0') !='1' UNION ALL select count(1) a from jsh_account_head ah inner join jsh_supplier s on s.id=ah.organ_id and ifnull(s.delete_Flag,'0') !='1' where s.type='${supType}' and ah.bill_time >='${beginTime}' and ah.bill_time <='${endTime}'
and ah.organ_id=${organId}
and ifnull(ah.delete_flag,'0') !='1' ) cc

