mapper.select:
select l.*,u.username userName FROM jsh_log l left join jsh_user u on l.user_id = u.id and ifnull(u.status,'0') not in('1','2') where 1=1
and l.operation like '%${operation}%'
and l.user_id=${userId}
and l.client_ip like '%${clientIp}%'
and l.status=${status}
and l.create_time >= '${beginTime}'
and l.create_time <= '${endTime}'
and l.content like '%${content}%'
order by l.create_time desc
limit #{offset},#{rows}
