mapper.select:
select m.* FROM jsh_material m where 1=1
and m.name =#{name}
and ifnull(m.delete_flag,'0') !='1'

select id, category_id, name, mfrs, safety_stock, model, standard, color, unit, remark, unit_id, enabled,enable_serial_number FROM jsh_material where 1=1 and enabled ='1' and enable_serial_number ='1'
and name like #{name}
and model like #{model}
and ifnull(delete_flag,'0') !='1' order by id desc

select count(1) FROM jsh_material where 1=1 and enabled ='1' and enable_serial_number ='1'
and name like #{name}
and model like #{model}
and ifnull(delete_flag,'0') !='1'

mapper.update:
update jsh_material set delete_flag='1' where 1=1 and ifnull(delete_flag,'0') !='1' and id in (
#{id}
)

mapper.select:
select
from jsh_material where 1=1 and category_id in (
#{categoryId}
) and ifnull(delete_flag,'0') !='1'

select
from jsh_material where 1=1 and unit_id in (
#{unitId}
) and ifnull(delete_flag,'0') !='1'

select m.*,me.bar_code m_bar_code,u.name unitName, mc.name categoryName FROM jsh_material m left join jsh_material_extend me on m.id=me.material_id and ifnull(me.delete_Flag,'0') !='1' left JOIN jsh_unit u on m.unit_id = u.id and ifnull(u.delete_flag,'0') !='1' left JOIN jsh_material_category mc on m.category_id = mc.id and ifnull(mc.status,'0') !='2' where 1=1
and me.id = ${meId}
and ifnull(m.delete_flag,'0') !='1'
