mapper.select:
select ser.id, ser.material_id, ser.serial_number, ser.is_sell, ser.remark, ser.delete_flag, ser.create_time, ser.update_time,mat.name as materialName,null as creator,null as updater,null as creatorName, null as updaterName,ser.depot_head_id, dh.number as depotHeadNumber,concat(dh.sub_type,dh.type) as depotHeadType FROM jsh_serial_number ser left join jsh_material mat on mat.id = ser.material_Id and ifnull(mat.delete_flag,'0') !='1' left join jsh_depot_head dh on dh.id= ser.depot_head_id and ifnull(dh.delete_flag,'0') !='1' where 1=1
and ser.serial_number like #{serialNumber}
and mat.name like #{materialName}
and ifnull(ser.delete_flag,'0') !='1' order by ser.id desc
limit #{offset},#{rows}

SELECT COUNT(ser.id) FROM jsh_serial_number ser left JOIN jsh_material mat on mat.id = ser.material_Id and ifnull(mat.delete_flag,'0') !='1' where 1=1
and ser.serial_Number like #{serialNumber}
and mat.name like #{materialName}
and ifnull(ser.delete_flag,'0') !='1' order by ser.id desc

SELECT ser.id, ser.material_id, ser.serial_number, ser.is_sell, ser.remark, ser.delete_flag, ser.create_time, ser.update_time,ser.creator,ser.updater,ser.depot_head_id FROM jsh_serial_number ser where 1=1
and ser.serial_number=#{serialNumber}
and ifnull(ser.delete_flag,'0') !='1' order by ser.id desc

SELECT count(ser.id) FROM jsh_serial_number ser where 1=1
and ser.material_Id=#{materialId}
and ifnull(ser.delete_flag,'0') !='1' and ser.is_sell !='1'

SELECT
count(ser.id)
FROM jsh_serial_number ser
where 1=1

    and ser.material_id=#{materialId}


    and ser.depot_head_id=#{depotHeadId}

and ser.is_sell =#{isSell,jdbcType=VARCHAR}
and ifnull(ser.delete_flag,'0') !='1'

mapper.update:
update jsh_serial_number

        is_sell = '0', depot_head_id=null,

        update_time = #{updateTime},


        updater = #{updater},


where 1=1

    and material_id = #{materialId}


    and depot_head_id = #{depotHeadId,jdbcType=BIGINT}

    and is_sell != '0'
    and ifnull(delete_flag,'0') !='1'

    and id in
    ( select batchSN.id from
    ( select selFrom.id from jsh_serial_number selFrom
    where 1=1

        and selFrom.material_id = #{materialId}


        and selFrom.depot_head_id = #{depotHeadId,jdbcType=BIGINT}

        and selFrom.is_sell !='0'
        and ifnull(selFrom.delete_flag,'0') !='1'
    limit 0,#{count}
    ) batchSN
    )

update jsh_serial_number
set update_time=#{updateTime},updater=#{updater},delete_flag='1'
where 1=1
and id in (

    #{id}

)
